package assignment3;

public class Month {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=1;
		String name;
		if(a==1)
			name="jan";
		else if(a==2)
			name="feb";
		else if(a==3)
			name="mar";
		else if(a==4)
			name="apr";
		else if(a==5)
			name="may";
		else if(a==6)
			name="june";
		else if(a==7)
			name="july";
		else if(a==8)
			name="aug";
		else if(a==9)
			name="sep";
		else if(a==10)
			name="oct";
		else if(a==11)
			name="nov";
		else if(a==12)
			name="dec";
		
		else
			name="enter numbers between 1-12";
		System.out.println(name);
			

	}

}
