package assignment3;

public class Week {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		String name;
		if(a==1)
			name="monday";
		else if(a==2)
			name="tuesday";
		else if(a==3)
			name="wednesday";
		else if(a==4)
			name="thursday";
		else if(a==5)
			name="friday";
		else if(a==6)
			name="saturday";
		else if(a==7)
			name="sunday";
		else
			name="enter numbers between 1-7";
		System.out.println(name);

	}

}
