package assignment4;

public class MaxofThree {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=90,b=10,c=4;
		if(a>b && a>c)
			System.out.println("A is big");
		else if(b>a && b>c)
			System.out.println("B is big");
		else if(c>a && c>b)
			System.out.println("C is big");
	}

}
